import React from "react";

import "./qustion.styles.scss";

import {
  selectRandom,
  selectAudioList,
  selectAnswered,
  selectTitle,
  selectBirdsInfo,
  selectBirdsNames,
} from "../../redux/birds/birds.selector";
import { useSelector } from "react-redux";

const Question = () => {
  const audioList = useSelector(selectAudioList);
  const randomVal = useSelector(selectRandom);
  const answered = useSelector(selectAnswered);
  const nameList = useSelector(selectBirdsNames);
  const title = useSelector(selectTitle);
  const birdsInfo = useSelector(selectBirdsInfo);
  return (
    <div className="question">
      <div>
        <img
          className="question-image"
          alt="unknown-bird"
          src={
            answered
              ? birdsInfo[randomVal].photos.photo[0].url_m
              : "https://www.google.com/url?sa=i&url=https%3A%2F%2Ffavpng.com%2Fpng_search%2Fblackbird&psig=AOvVaw0S6TqDWwUzP8dM5zs-2ipu&ust=1617442186686000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCLCigu2f3-8CFQAAAAAdAAAAABAD"
          }
        />
      </div>
      <div className="question-data">
        <div className="name">
          {answered ? nameList[randomVal][0] : "******"}
        </div>
        <audio
          className="question-audio"
          controls
          src={audioList[randomVal]}
        ></audio>
      </div>
    </div>
  );
};

export default Question;
