import { takeLatest, call, put, all } from "redux-saga/effects";

import birdsActionTypes from "./birds.types";

import { fetchImageFailure, fetchImageSuccess } from "./birds.action";

export function* onFetchImageStart() {
  yield takeLatest(birdsActionTypes.FETCH_SONG_START, fetchingImage);
}

export function* fetchingImage() {
  try {
    const data = yield fetch(
      "https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=0f15ff623f1198a1f7f52550f8c36057&tag_mode=all&extras=url_m&format=json&nojsoncallback=1&tags=соловей"
    );
    console.log(data);
    yield put(fetchImageSuccess(data));
  } catch (error) {
    yield put(fetchImageFailure(error));
  }
}
